import { Component, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Tarefa, TarefaService } from '../shared';
import { Router } from '@angular/router';

@Component({
  selector: 'app-cadastrar',
  templateUrl: './cadastrar-tarefa.component.html',
  styleUrls: ['./cadastrar-tarefa.component.css']
})
export class CadastrarTarefaComponent implements OnInit{

  @ViewChild("formTarefa", {static: true}) formTarefa: NgForm;
  tarefa: Tarefa;

  constructor(
    private tarefaService: TarefaService,
    private router: Router){}

    ngOnInit(){
        this.tarefa = new Tarefa();
    }

    cadastrar(): void {
      if (this.formTarefa.form.valid) {
        this.tarefaService.cadastrar(this.tarefa);
        this.router.navigate(["/tarefas"]);
      }
    }
}
<h1>Cadastrar tarefa</h1>

<div class="well">
    <form #formTarefa="ngForm">
        <div class="form-group">
            <label for="nome">Tarefa:</label>
            <input [(ngModel)]="tarefa.nome" #nome="ngModel" type="text" class="form-control" id="nome" name="nome" minlength="5" required> 
            <div *ngIf="nome.errors && (nome.dirty || nome.touched)" class="alert alert-danger">
                <div [hidden]="!nome.errors.required">
                    Digite a tarefa.
                </div>
                <div [hidden]="!nome.errors.minlength">
                    A tarefa deve conter ao menos 5 caracteres.
                </div>
            </div>
        </div>
        <div class="form-group text-center">
            <input type="submit" class="btn btn-success" value="Cadastrar" [disabled]="!formTarefa.form.valid" (click)="cadastrar()">
            <a [routerLink]="['/tarefas']" class="btn btn-default"> <span class="glyphicon glyphicon-chevron-left" aria-hidden="true"></span> Voltar </a>
        </div>
    </form>
</div>
